import React from 'react'
import { Cards, CardItem }  from 'vertical-timeline-component-for-react'
import Board from '../Boad'

const Card = (props) => {
    const board  = props.data.map((data) =>{
        
    return (
        <Cards animate={false} lineColor={'#00C853'}>
            {
                board.map((c, key) => (
                    <CardItem key={key} dateStyle={{ display: 'none' }}>
                        <Board board={c}/>
                    </CardItem>
                ))
            }
        </Cards>
    )
    })
}

export default Card
