import styled from 'styled-components';

export const Container = styled.div`

@mixin timeline-indicator {
	content: "";
	display: inline-block;
	width: 1em;
	height: 1em;
	position: absolute;
	border-top: 10px solid transparent;
	border-bottom: 10px solid transparent;
}


@mixin timeline-entry {
	@for $i from 1 through 5 {
		&:nth-child(#{$i}) {
			grid-area: entry#{$i};
		}
	}
}


$timeline-indicator-color: $seagull;
$timeline-indicator-border:  1px solid $gondola;
$timeline-title-background-color: $dark-pink;

.timeline {
	line-height: 1.5em;
	font-size: 14px;
	transition: all .4s ease;
	position: relative;
	counter-reset: section;
	

	&:before {
		content:"";
		width: 10px;
		height: 100%;
		background: $timeline-indicator-color;
		position: absolute;
		top: 0;
		left: -3.313em;
		border-radius: 40px 40px;
	}
}

.timeline--entry {
	position: relative;
	background-color: $panel-background-color;

	&__title {
		color: white;
		background-color: $timeline-title-background-color;
		font-family: $subheading-font-family;
		font-weight: $font-weight-light;
		font-size: 1rem;
		padding: 1em;
		&:before {
			@include timeline-indicator;
			left: -1em;
			border-right: 10px solid $dark-pink; 
		}
	}
	
	&__detail {
		background-color: $alto;
		padding: 1em;
		margin: 0;
	}
			
	&:before {
		content: "";
		font-family: $secondary-font-family;
		color: $white;
		display: flex;
		align-items: center;
		justify-content: center;
		width: 3em;
		height: 3em;
		background-color: $seagull;
		border: .2em solid white;
		border-radius: 50%;
		position: absolute;
		counter-increment: section;
  	content: counter(section);
		text-shadow: 0 1px 0 $charcoal;
		left: -4.5em;
	}
}

@supports (display: grid) {
	@include tablet-up {
		.timeline {
			display: grid;
			grid-gap: 4.75em;
			grid-template-areas: 	".  entry1"
														"entry2  ."
														". entry3"
														"entry4 ."
														". entry5";
			//- Timeline line
			&:before {
				left: 49.5%;
			}
		}
		
		.timeline--entry {
				@include timeline-entry;
			
				//- odd timeline entry
				&:nth-of-type(odd){
					//- Default position the number indicator mobile
					&:before {
						left: -3.8em;
					}
				}
				
			 //- even timeline entry	
				&:nth-of-type(even){
					&:not(:nth-of-type(odd)) {
						//-right pointing arrow indicator
						.timeline--entry__title {
							&:before {
								left: 100% ;
								border-left: 10px solid $dark-pink;
								border-right: 0;
							}
						}
					}
					//- Position the number indicator
					&:before {
						left: 103%;
					}
				}	
			}
		}
	}
`;
